PRAGMA foreign_keys=OFF;
BEGIN TRANSACTION;
CREATE TABLE workspace_tasks (
	id VARCHAR(50) NOT NULL, 
	title VARCHAR(500) NOT NULL, 
	description TEXT, 
	status VARCHAR(50) NOT NULL, 
	priority VARCHAR(20), 
	repos_affected TEXT, 
	dependencies TEXT, 
	created DATETIME NOT NULL, 
	updated DATETIME NOT NULL, 
	session_created VARCHAR(100), 
	assigned_to VARCHAR(100), 
	notes TEXT, 
	related_files TEXT, 
	extra_metadata TEXT, workflow_id TEXT, assigned_repo TEXT, assigned_by TEXT, bastard_plan_grade TEXT, bastard_completion_grade TEXT, scale_tier INTEGER, started_at TIMESTAMP, completed_at TIMESTAMP, 
	PRIMARY KEY (id)
);
CREATE TABLE workspace_sessions (
	id VARCHAR(100) NOT NULL, 
	start_time DATETIME NOT NULL, 
	end_time DATETIME, 
	status VARCHAR(50) NOT NULL, 
	user VARCHAR(100), 
	ai_assistant VARCHAR(100), 
	handoff_notes TEXT, 
	PRIMARY KEY (id)
);
CREATE TABLE architecture_decisions (
	id VARCHAR(50) NOT NULL, 
	date DATETIME NOT NULL, 
	session VARCHAR(100) NOT NULL, 
	decision TEXT NOT NULL, 
	repos_affected TEXT, 
	rationale TEXT, 
	status VARCHAR(50) NOT NULL, 
	impact VARCHAR(20), 
	related_files TEXT, 
	documentation TEXT, 
	extra_metadata TEXT, 
	PRIMARY KEY (id)
);
CREATE TABLE architecture_states (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	state_type VARCHAR(50) NOT NULL, 
	version VARCHAR(20) NOT NULL, 
	architecture_id VARCHAR(50), 
	description TEXT, 
	roadmap_file VARCHAR(500), 
	target_date DATETIME, 
	status VARCHAR(50) NOT NULL, 
	created_at DATETIME NOT NULL, 
	updated_at DATETIME NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE drift_scans (
	id VARCHAR(50) NOT NULL, 
	scan_type VARCHAR(50) NOT NULL, 
	scan_start DATETIME NOT NULL, 
	scan_end DATETIME, 
	repos_scanned TEXT, 
	violations_found INTEGER, 
	violations_high INTEGER, 
	violations_medium INTEGER, 
	violations_low INTEGER, 
	status VARCHAR(50) NOT NULL, 
	triggered_by VARCHAR(100), 
	results_file VARCHAR(500), 
	PRIMARY KEY (id)
);
CREATE TABLE context_switches (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	repo_path VARCHAR(500) NOT NULL, 
	activated_at DATETIME NOT NULL, 
	deactivated_at DATETIME, 
	activated_by VARCHAR(100), 
	context_id VARCHAR(50), 
	previous_context VARCHAR(100), 
	context_notes TEXT, 
	duration_seconds FLOAT, 
	operations_count INTEGER, 
	PRIMARY KEY (id), 
	UNIQUE (context_id)
);
CREATE TABLE configuration_snapshots (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	snapshot_type VARCHAR(50) NOT NULL, 
	configuration_hash VARCHAR(64) NOT NULL, 
	snapshot_data TEXT, 
	taken_at DATETIME NOT NULL, 
	taken_by VARCHAR(100), 
	notes TEXT, 
	PRIMARY KEY (id)
);
CREATE TABLE session_activities (
	id VARCHAR(50) NOT NULL, 
	session_id VARCHAR(100) NOT NULL, 
	activity_type VARCHAR(50) NOT NULL, 
	description TEXT NOT NULL, 
	files_created TEXT, 
	files_modified TEXT, 
	outcome TEXT, 
	time DATETIME NOT NULL, 
	extra_metadata TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(session_id) REFERENCES workspace_sessions (id) ON DELETE CASCADE
);
CREATE TABLE session_decisions (
	id VARCHAR(50) NOT NULL, 
	session_id VARCHAR(100) NOT NULL, 
	decision_id VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(session_id) REFERENCES workspace_sessions (id) ON DELETE CASCADE, 
	FOREIGN KEY(decision_id) REFERENCES architecture_decisions (id)
);
CREATE TABLE cross_repo_issues (
	id VARCHAR(50) NOT NULL, 
	issue_type VARCHAR(50) NOT NULL, 
	severity VARCHAR(20) NOT NULL, 
	title VARCHAR(500) NOT NULL, 
	description TEXT, 
	repos_affected TEXT, 
	detected DATETIME NOT NULL, 
	detected_by VARCHAR(100), 
	status VARCHAR(50) NOT NULL, 
	action_required TEXT, 
	assigned_to VARCHAR(100), 
	related_task_id VARCHAR(50), 
	resolved_at DATETIME, 
	resolved_by VARCHAR(100), 
	resolution_notes TEXT, 
	extra_metadata TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(related_task_id) REFERENCES workspace_tasks (id)
);
CREATE TABLE component_placements (
	id VARCHAR(50) NOT NULL, 
	component_name VARCHAR(200) NOT NULL, 
	component_type VARCHAR(50) NOT NULL, 
	correct_repo VARCHAR(100) NOT NULL, 
	correct_location VARCHAR(500), 
	architecture_state_id VARCHAR(50), 
	rationale TEXT, 
	rules TEXT, 
	created_at DATETIME NOT NULL, 
	updated_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	CONSTRAINT uq_component_placement UNIQUE (component_name, architecture_state_id), 
	FOREIGN KEY(architecture_state_id) REFERENCES architecture_states (id)
);
CREATE TABLE drift_detections (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	violation_type VARCHAR(50) NOT NULL, 
	severity VARCHAR(20) NOT NULL, 
	file_path VARCHAR(500) NOT NULL, 
	component_name VARCHAR(200), 
	detected_rule VARCHAR(200), 
	expected_location VARCHAR(500), 
	actual_location VARCHAR(500), 
	violation_details TEXT, 
	detected_at DATETIME NOT NULL, 
	detected_by VARCHAR(100), 
	status VARCHAR(50) NOT NULL, 
	resolved_at DATETIME, 
	resolved_by VARCHAR(100), 
	resolution_notes TEXT, 
	related_task_id VARCHAR(50), 
	extra_metadata TEXT, 
	PRIMARY KEY (id), 
	FOREIGN KEY(related_task_id) REFERENCES workspace_tasks (id)
);
CREATE TABLE session_issues (
	id VARCHAR(50) NOT NULL, 
	session_id VARCHAR(100) NOT NULL, 
	issue_id VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(session_id) REFERENCES workspace_sessions (id) ON DELETE CASCADE, 
	FOREIGN KEY(issue_id) REFERENCES cross_repo_issues (id)
);
CREATE TABLE architecture_tasks (
	id VARCHAR(50) NOT NULL, 
	task_id VARCHAR(50) NOT NULL, 
	architecture_state_id VARCHAR(50), 
	architectural_goal TEXT, 
	component_id VARCHAR(50), 
	milestone VARCHAR(200), 
	priority VARCHAR(20), 
	status VARCHAR(50) NOT NULL, 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(task_id) REFERENCES workspace_tasks (id), 
	FOREIGN KEY(architecture_state_id) REFERENCES architecture_states (id), 
	FOREIGN KEY(component_id) REFERENCES component_placements (id)
);
CREATE TABLE configuration_changes (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	change_type VARCHAR(50) NOT NULL, 
	component_name VARCHAR(200), 
	file_path VARCHAR(500), 
	old_value TEXT, 
	new_value TEXT, 
	changed_at DATETIME NOT NULL, 
	changed_by VARCHAR(100), 
	commit_hash VARCHAR(100), 
	drift_detection_id VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(drift_detection_id) REFERENCES drift_detections (id)
);
CREATE TABLE work_contexts (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	repo_path VARCHAR(500) NOT NULL, 
	activated_at DATETIME NOT NULL, 
	activated_by VARCHAR(100), 
	is_active BOOLEAN, 
	notes TEXT, 
	previous_context_id VARCHAR(50), 
	PRIMARY KEY (id), 
	FOREIGN KEY(previous_context_id) REFERENCES work_contexts (id)
);
CREATE TABLE workflows (
	id VARCHAR(50) NOT NULL, 
	workflow_id VARCHAR(20), 
	name VARCHAR(500) NOT NULL, 
	workflow_type VARCHAR(50), 
	allowed_repos TEXT, 
	status VARCHAR(20), 
	current_stage INTEGER, 
	created_at DATETIME NOT NULL, 
	completed_at DATETIME, 
	PRIMARY KEY (id), 
	UNIQUE (workflow_id)
);
CREATE TABLE violations (
	id VARCHAR(50) NOT NULL, 
	task_id VARCHAR(50), 
	violation_type VARCHAR(50) NOT NULL, 
	severity VARCHAR(20) NOT NULL, 
	message TEXT NOT NULL, 
	file_path VARCHAR(500), 
	line_number INTEGER, 
	rule_violated VARCHAR(500), 
	fix_required TEXT, 
	status VARCHAR(20), 
	resolved_at DATETIME, 
	detected_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(task_id) REFERENCES workspace_tasks (id)
);
CREATE TABLE import_rules (
	id VARCHAR(50) NOT NULL, 
	source_repo VARCHAR(100) NOT NULL, 
	rule_type VARCHAR(20) NOT NULL, 
	target_module VARCHAR(200), 
	reason TEXT NOT NULL, 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE scale_tiers (
	id VARCHAR(50) NOT NULL, 
	domain VARCHAR(50) NOT NULL, 
	tier INTEGER NOT NULL, 
	name VARCHAR(200) NOT NULL, 
	user_range VARCHAR(100), 
	solution VARCHAR(500), 
	complexity INTEGER, 
	upgrade_trigger TEXT, 
	migration_effort VARCHAR(100), 
	created_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	CONSTRAINT uq_scale_tier_domain UNIQUE (domain, tier)
);
CREATE TABLE bastard_reports (
	id VARCHAR(50) NOT NULL, 
	task_id VARCHAR(50), 
	evaluation_type VARCHAR(50) NOT NULL, 
	overall_grade VARCHAR(1), 
	scale_appropriateness VARCHAR(1), 
	deployment_maturity VARCHAR(1), 
	portability VARCHAR(1), 
	state_management VARCHAR(1), 
	credential_management VARCHAR(1), 
	actual_users INTEGER, 
	designed_for_users INTEGER, 
	over_engineering_score INTEGER, 
	tier_recommendation INTEGER, 
	critical_blockers TEXT, 
	required_fixes TEXT, 
	full_report TEXT, 
	evaluated_at DATETIME NOT NULL, 
	PRIMARY KEY (id), 
	FOREIGN KEY(task_id) REFERENCES workspace_tasks (id)
);
CREATE TABLE workflow_stages (
	id VARCHAR(50) NOT NULL, 
	workflow_id VARCHAR(50) NOT NULL, 
	stage_number INTEGER NOT NULL, 
	name VARCHAR(200) NOT NULL, 
	validation_required BOOLEAN, 
	validation_type VARCHAR(50), 
	status VARCHAR(20), 
	completed_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(workflow_id) REFERENCES workflows (id)
);
CREATE TABLE architecture_states_old (
	id VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	state_type VARCHAR(50) NOT NULL, 
	version VARCHAR(20) NOT NULL, 
	architecture_id VARCHAR(50), 
	description TEXT, 
	roadmap_file VARCHAR(500), 
	target_date DATETIME, 
	status VARCHAR(50) NOT NULL, 
	created_at DATETIME NOT NULL, 
	updated_at DATETIME NOT NULL, 
	PRIMARY KEY (id)
);
CREATE TABLE architecture_components (
	id VARCHAR(50) NOT NULL, 
	component_name VARCHAR(200) NOT NULL, 
	component_type VARCHAR(50) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	expected_path VARCHAR(500), 
	description TEXT, 
	approved_scope TEXT, 
	boundaries TEXT, 
	status VARCHAR(50) NOT NULL, 
	version VARCHAR(50), 
	created_at DATETIME NOT NULL, 
	updated_at DATETIME NOT NULL, 
	registered_by VARCHAR(100), 
	architecture_state_id VARCHAR(50), 
	PRIMARY KEY (id), 
	CONSTRAINT uq_component_name_repo UNIQUE (component_name, repo), 
	FOREIGN KEY(architecture_state_id) REFERENCES architecture_states (id)
);
CREATE TABLE code_component_mappings (
	id VARCHAR(50) NOT NULL, 
	file_path VARCHAR(500) NOT NULL, 
	component_id VARCHAR(50) NOT NULL, 
	mapping_type VARCHAR(50), 
	mapping_reason TEXT, 
	is_validated BOOLEAN, 
	validated_by VARCHAR(100), 
	validated_at DATETIME, 
	created_at DATETIME NOT NULL, 
	created_by VARCHAR(100), 
	PRIMARY KEY (id), 
	CONSTRAINT uq_file_path UNIQUE (file_path), 
	FOREIGN KEY(component_id) REFERENCES architecture_components (id)
);
CREATE TABLE unregistered_files (
	id VARCHAR(50) NOT NULL, 
	file_path VARCHAR(500) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	first_detected DATETIME NOT NULL, 
	last_seen DATETIME NOT NULL, 
	detection_count INTEGER, 
	status VARCHAR(50), 
	assigned_component_id VARCHAR(50), 
	review_notes TEXT, 
	reviewed_by VARCHAR(100), 
	reviewed_at DATETIME, 
	PRIMARY KEY (id), 
	FOREIGN KEY(assigned_component_id) REFERENCES architecture_components (id)
);
CREATE TABLE code_changes (
	id VARCHAR(50) NOT NULL, 
	commit_hash VARCHAR(100) NOT NULL, 
	repo VARCHAR(100) NOT NULL, 
	change_type VARCHAR(50) NOT NULL, 
	file_path VARCHAR(500) NOT NULL, 
	component_id VARCHAR(50), 
	mapping_id VARCHAR(50), 
	is_tracked BOOLEAN, 
	is_validated BOOLEAN, 
	validation_status VARCHAR(50), 
	validation_notes TEXT, 
	within_component_scope BOOLEAN, 
	scope_violations TEXT, 
	lines_added INTEGER, 
	lines_removed INTEGER, 
	lines_modified INTEGER, 
	changed_at DATETIME NOT NULL, 
	changed_by VARCHAR(100), 
	commit_message TEXT, 
	branch VARCHAR(200), 
	PRIMARY KEY (id), 
	FOREIGN KEY(component_id) REFERENCES architecture_components (id), 
	FOREIGN KEY(mapping_id) REFERENCES code_component_mappings (id)
);
